import React, { useEffect, useState } from "react";
import api from "../api/axios";

function Dashboard() {
  const [readings, setReadings] = useState([]);
  const [metricType, setMetricType] = useState("weight");
  const [value, setValue] = useState("");
  const [unit, setUnit] = useState("");
  const [date, setDate] = useState("");
  const [editingId, setEditingId] = useState(null);

  const token = localStorage.getItem("access");

  const fetchReadings = async () => {
    try {
      const res = await api.get("/api/readings/", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const sorted = res.data.sort((a, b) => new Date(b.date) - new Date(a.date));
      setReadings(sorted);
    } catch (err) {
      console.error("Error fetching readings:", err.response?.data || err.message);
      alert("Error fetching data");
    }
  };

  useEffect(() => {
    fetchReadings();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const payload = { metric_type: metricType, value, unit, date };

    try {
      if (editingId) {
        await api.put(`/api/readings/${editingId}/`, payload, {
          headers: { Authorization: `Bearer ${token}` },
        });
      } else {
        await api.post("/api/readings/", payload, {
          headers: { Authorization: `Bearer ${token}` },
        });
      }

      fetchReadings();
      resetForm();
    } catch (err) {
      console.error("Error saving reading:", err.response?.data || err.message);
      alert("Error saving reading.");
    }
  };

  const handleEdit = (reading) => {
    setMetricType(reading.metric_type);
    setValue(reading.value);
    setUnit(reading.unit);
    setDate(reading.date);
    setEditingId(reading.id);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this reading?")) return;

    try {
      await api.delete(`/api/readings/${id}/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchReadings();
    } catch (err) {
      console.error("Error deleting reading:", err.response?.data || err.message);
      alert("Error deleting reading.");
    }
  };

  const resetForm = () => {
    setMetricType("weight");
    setValue("");
    setUnit("");
    setDate("");
    setEditingId(null);
  };

  const handleLogout = () => {
    localStorage.removeItem("access");
    localStorage.removeItem("refresh");
    window.location.href = "/login";
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-3xl mx-auto bg-white rounded-xl shadow-md p-6">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-2xl font-bold text-gray-800">My Health Readings</h2>
          <button
            onClick={handleLogout}
            className="text-sm text-white bg-red-500 hover:bg-red-600 px-4 py-2 rounded"
          >
            Logout
          </button>
        </div>

        <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
          <select
            value={metricType}
            onChange={(e) => setMetricType(e.target.value)}
            className="border rounded px-3 py-2"
          >
            <option value="weight">Weight</option>
            <option value="blood_pressure">Blood Pressure</option>
            <option value="blood_sugar">Blood Sugar</option>
          </select>

          <input
            type="number"
            placeholder="Value"
            value={value}
            onChange={(e) => setValue(e.target.value)}
            required
            className="border rounded px-3 py-2"
          />
          <input
            placeholder="Unit"
            value={unit}
            onChange={(e) => setUnit(e.target.value)}
            required
            className="border rounded px-3 py-2"
          />
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
            className="border rounded px-3 py-2"
          />
          <div className="md:col-span-4 flex gap-2">
            <button
              type="submit"
              className="w-full bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
              {editingId ? "Update Reading" : "Add Reading"}
            </button>
            {editingId && (
              <button
                type="button"
                onClick={resetForm}
                className="w-full bg-gray-300 text-gray-800 px-4 py-2 rounded hover:bg-gray-400"
              >
                Cancel
              </button>
            )}
          </div>
        </form>

        <h3 className="text-xl font-semibold mb-4">Logged Metrics:</h3>

        {readings.length === 0 ? (
          <p className="text-gray-600">No readings yet. Add one above!</p>
        ) : (
          <ul className="space-y-3">
            {readings.map((r) => (
              <li
                key={r.id}
                className="bg-gray-50 p-4 rounded border border-gray-200 shadow-sm flex justify-between items-center"
              >
                <div>
                  <div className="text-sm text-gray-500">{new Date(r.date).toLocaleDateString()}</div>
                  <div className="text-lg font-medium text-gray-800 capitalize">
                    {r.metric_type.replace("_", " ")}: {r.value} {r.unit}
                  </div>
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={() => handleEdit(r)}
                    className="text-sm bg-yellow-400 hover:bg-yellow-500 text-white px-3 py-1 rounded"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDelete(r.id)}
                    className="text-sm bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded"
                  >
                    Delete
                  </button>
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
