import React, { useEffect, useState } from "react";
import api from "../api/axios";

function Dashboard() {
  const [readings, setReadings] = useState([]);
  const [metricType, setMetricType] = useState("weight");
  const [value, setValue] = useState("");
  const [unit, setUnit] = useState("");
  const [date, setDate] = useState("");
  const [editingId, setEditingId] = useState(null);
  const [filterType, setFilterType] = useState("all");
  const [sortOrder, setSortOrder] = useState("desc");

  const token = localStorage.getItem("access");

  const fetchReadings = async () => {
    try {
      const res = await api.get("/api/readings/", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setReadings(res.data);
    } catch (err) {
      console.error("Error fetching readings:", err.response?.data || err.message);
      alert("Error fetching data");
    }
  };

  useEffect(() => {
    fetchReadings();
  }, []);

  const resetForm = () => {
    setMetricType("weight");
    setValue("");
    setUnit("");
    setDate("");
    setEditingId(null);
  };

  const handleAddOrUpdate = async (e) => {
    e.preventDefault();
    const payload = { metric_type: metricType, value, unit, date };

    try {
      if (editingId) {
        await api.put(`/api/readings/${editingId}/`, payload, {
          headers: { Authorization: `Bearer ${token}` },
        });
      } else {
        await api.post("/api/readings/", payload, {
          headers: { Authorization: `Bearer ${token}` },
        });
      }

      fetchReadings();
      resetForm();
    } catch (err) {
      console.error("Error saving reading:", err.response?.data || err.message);
      alert("Error saving reading.");
    }
  };

  const handleEdit = (reading) => {
    setEditingId(reading.id);
    setMetricType(reading.metric_type);
    setValue(reading.value);
    setUnit(reading.unit);
    setDate(reading.date);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this reading?")) return;
    try {
      await api.delete(`/api/readings/${id}/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchReadings();
    } catch (err) {
      console.error("Error deleting reading:", err.response?.data || err.message);
      alert("Failed to delete.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("access");
    localStorage.removeItem("refresh");
    window.location.reload();
  };

  const filtered = readings
    .filter((r) => (filterType === "all" ? true : r.metric_type === filterType))
    .sort((a, b) => (sortOrder === "asc" ? a.date.localeCompare(b.date) : b.date.localeCompare(a.date)));

  const latestReading = filtered[0];

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2>My Health Readings</h2>
      <button onClick={handleLogout} style={{ float: "right", marginBottom: "10px" }}>
        Logout
      </button>

      {latestReading && (
        <p style={{ fontWeight: "bold", marginTop: "20px" }}>
          Latest {latestReading.metric_type} Reading: {latestReading.value} {latestReading.unit}
        </p>
      )}

      <form onSubmit={handleAddOrUpdate} style={{ marginBottom: "20px" }}>
        <select value={metricType} onChange={(e) => setMetricType(e.target.value)}>
          <option value="weight">Weight</option>
          <option value="blood_pressure">Blood Pressure</option>
          <option value="blood_sugar">Blood Sugar</option>
        </select>
        <input
          type="number"
          placeholder="Value"
          value={value}
          onChange={(e) => setValue(e.target.value)}
          required
        />
        <input
          placeholder="Unit"
          value={unit}
          onChange={(e) => setUnit(e.target.value)}
          required
        />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
        <button type="submit">{editingId ? "Update" : "Add"}</button>
        {editingId && <button onClick={resetForm}>Cancel</button>}
      </form>

      <div style={{ marginBottom: "10px" }}>
        <label>Filter by Type: </label>
        <select value={filterType} onChange={(e) => setFilterType(e.target.value)}>
          <option value="all">All</option>
          <option value="weight">Weight</option>
          <option value="blood_pressure">Blood Pressure</option>
          <option value="blood_sugar">Blood Sugar</option>
        </select>

        <label style={{ marginLeft: "15px" }}>Sort by Date: </label>
        <select value={sortOrder} onChange={(e) => setSortOrder(e.target.value)}>
          <option value="desc">Newest First</option>
          <option value="asc">Oldest First</option>
        </select>
      </div>

      <ul>
        {filtered.map((r) => (
          <li key={r.id} style={{ marginBottom: "8px" }}>
            <strong>{r.date}</strong> - {r.metric_type}: {r.value} {r.unit}
            <button onClick={() => handleEdit(r)} style={{ marginLeft: "10px" }}>Edit</button>
            <button onClick={() => handleDelete(r.id)} style={{ marginLeft: "5px" }}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
