import React, { useEffect, useState } from "react";
import api from "../api/axios";

function Dashboard() {
  const [readings, setReadings] = useState([]);
  const [filteredReadings, setFilteredReadings] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [form, setForm] = useState({
    metric_type: "weight",
    value: "",
    unit: "",
    date: "",
  });
  const [filterType, setFilterType] = useState("all");
  const [sortOrder, setSortOrder] = useState("desc");

  const token = localStorage.getItem("access");

  const fetchReadings = async () => {
    try {
      const res = await api.get("/api/readings/", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setReadings(res.data);
    } catch (err) {
      console.error("Error fetching readings:", err.response?.data || err.message);
      alert("Error fetching data");
    }
  };

  useEffect(() => {
    fetchReadings();
  }, []);

  useEffect(() => {
    let filtered = [...readings];
    if (filterType !== "all") {
      filtered = filtered.filter((r) => r.metric_type === filterType);
    }
    filtered.sort((a, b) => {
      return sortOrder === "asc"
        ? new Date(a.date) - new Date(b.date)
        : new Date(b.date) - new Date(a.date);
    });
    setFilteredReadings(filtered);
  }, [readings, filterType, sortOrder]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await api.put(`/api/readings/${editingId}/`, form, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setEditingId(null);
      } else {
        await api.post("/api/readings/", form, {
          headers: { Authorization: `Bearer ${token}` },
        });
      }
      setForm({ metric_type: "weight", value: "", unit: "", date: "" });
      fetchReadings();
    } catch (err) {
      console.error("Error saving reading:", err.response?.data || err.message);
      alert("Error saving reading.");
    }
  };

  const handleEdit = (reading) => {
    setForm({
      metric_type: reading.metric_type,
      value: reading.value,
      unit: reading.unit,
      date: reading.date,
    });
    setEditingId(reading.id);
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this reading?")) {
      try {
        await api.delete(`/api/readings/${id}/`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        fetchReadings();
      } catch (err) {
        console.error("Error deleting reading:", err.response?.data || err.message);
        alert("Error deleting reading.");
      }
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("access");
    localStorage.removeItem("refresh");
    window.location.href = "/";
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      <h2>My Health Metrics</h2>
      <button onClick={handleLogout} style={{ float: "right" }}>
        Logout
      </button>

      <form onSubmit={handleSubmit} style={{ marginBottom: "1rem" }}>
        <select
          value={form.metric_type}
          onChange={(e) => setForm({ ...form, metric_type: e.target.value })}
          required
        >
          <option value="weight">Weight</option>
          <option value="blood_pressure">Blood Pressure</option>
          <option value="blood_sugar">Blood Sugar</option>
        </select>
        <input
          type="number"
          placeholder="Value"
          value={form.value}
          onChange={(e) => setForm({ ...form, value: e.target.value })}
          required
        />
        <input
          placeholder="Unit"
          value={form.unit}
          onChange={(e) => setForm({ ...form, unit: e.target.value })}
          required
        />
        <input
          type="date"
          value={form.date}
          onChange={(e) => setForm({ ...form, date: e.target.value })}
          required
        />
        <button type="submit">{editingId ? "Update" : "Add"}</button>
      </form>

      <div style={{ marginBottom: "1rem" }}>
        <label>Filter: </label>
        <select value={filterType} onChange={(e) => setFilterType(e.target.value)}>
          <option value="all">All</option>
          <option value="weight">Weight</option>
          <option value="blood_pressure">Blood Pressure</option>
          <option value="blood_sugar">Blood Sugar</option>
        </select>

        <label style={{ marginLeft: "1rem" }}>Sort by date: </label>
        <select value={sortOrder} onChange={(e) => setSortOrder(e.target.value)}>
          <option value="desc">Newest First</option>
          <option value="asc">Oldest First</option>
        </select>
      </div>

      <table border="1" cellPadding="8" style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th>Date</th>
            <th>Metric</th>
            <th>Value</th>
            <th>Unit</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredReadings.map((r) => (
            <tr key={r.id}>
              <td>{r.date}</td>
              <td>{r.metric_type}</td>
              <td>{r.value}</td>
              <td>{r.unit}</td>
              <td>
                <button onClick={() => handleEdit(r)}>Edit</button>{" "}
                <button onClick={() => handleDelete(r.id)} style={{ color: "red" }}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
          {filteredReadings.length === 0 && (
            <tr>
              <td colSpan="5" align="center">No records found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}

export default Dashboard;
